import React, { Component } from 'react';

class Header extends Component {

  state = {
    header: ''
  }

  contactClick=()=>{
    window.scrollTo(0,document.body.scrollHeight)
  }

  aboutClick=()=>{
    let scrollAbout=()=>{
      window.scrollTo(0,0)
      window.scrollBy({ top: window.innerHeight, left: 0, behavior: 'smooth' })
    }
    this.setState({
      header: 'about'
    }, scrollAbout())

  }

  portfolioClick=()=>{
    console.log('Portfolio')
  }


  shelbyClick =()=>{
    window.scrollTo(0,-document.body.scrollHeight)
  }

  render() {
    return (
      <nav id="header">
        <div className="headerHover" id="contact" onClick={()=>this.contactClick()}>Contact</div>
        <div className="headerHover" id="shelby" onClick={()=>this.shelbyClick()}>SHELBY</div>
        <div id='navRight'>
          <div className="headerHover" onClick={()=>this.aboutClick()}>About</div>
          <div className="headerHover" onClick={()=>this.portfolioClick()}>Portfolio</div>
        </div>
      </nav>
    )
  }
}

export default Header
